INSERT INTO `disease` VALUES ('1', '1', '高血压', '血液', '高血压是指动脉内血液压力过高。', '', '头痛、眩晕或轻微头重、复视、鼻出血、心跳加速、耳鸣、肌肉抽搐，严重时还会恶心、呕吐甚至精神错乱', '', '如果你有高血压，避免吃含盐分的食物 ，也不要在食物中加盐。如果你没有高血压，避免含盐食物是否能预防高血压则是一个有争议的问题。有些人对盐敏感，少吃含盐食物会更有益。最新的研究也表明，对60岁以上以及非裔美国人来说，减少盐分的摄取会非常有益。\r\n保持体形', '自检或寻找医生', '', '', '', '', '0');
INSERT INTO `disease` VALUES ('2', '1', '糖尿病', '全身', '糖尿病是一组以高血糖为特征的代谢性疾病。高血糖则是由于胰岛素分泌缺陷或其生物作用受损，或两者兼有引起。糖尿病时长期存在的高血糖，导致各种组织，特别是眼、肾、心脏、血管、神经的慢性损害、功能障碍', '', '多饮、多尿、多食和消瘦，疲乏无力，肥胖', '', '口服药物治疗、胰岛素治疗、饮食治疗', '自检或寻找医生', '', '', '', '', '0');
INSERT INTO `disease` VALUES ('3', '1', '甲状腺癌', '甲状腺', '甲状腺恶性肿瘤', '放射因素', ' 腺体出现一个或数个硬块或结节。结节随着时间增长，变得形状不规则而且变硬。一般无痛感。', '', ' ', '抽血化验', ' ', ' ', ' ', '', '0');
INSERT INTO `disease` VALUES ('4', '1', '垂体功能衰退', '脑', '垂体前叶功能减退症系腺垂体分泌激素不足所引起的征群', '腺垂体分泌激素不足', '甲状腺功能减退症，肾上腺皮质类固醇激素水平低下，如皮质醇、醛固酮，以及性功能减退或无法生育。 ', '甲状腺功能减退症，肾上腺皮质类固醇激素水平低下', '治疗方法包括终身口服药物或进行注射，以此取代体内产生不足的激素。', '激素检查', ' ', ' ', ' ', ' ', '0');
INSERT INTO `disease` VALUES ('5', '1', '垂体瘤', '脑', '垂体瘤是一组从垂体前叶和后叶及颅咽管上皮残余细胞发生的肿瘤。', ' ', '小的肿瘤生成大量的催乳激素会导致没有月经和不孕不育，这种情况是可以进行治疗的。肿瘤也可能导致女性怀孕和分娩时没有母乳，男性则会导致阳痿。', '月经和不孕不育', '手术，放射治疗', ' ', ' ', ' ', ' ', ' ', '0');
INSERT INTO `disease` VALUES ('6', '1', '心动过缓', '心脏', '心动过缓是指每分钟少于60次。', '服用了过多降心率的药物、心脏传导阻滞和病窦综合征。', '疲劳心率急剧下降会引起无力、头晕、晕眩甚至心搏骤停。如果你的心跳慢到无法给大脑供应足够的血 液，则会引起脑卒中。', ' ', '医生需要开展紧急治疗来恢复你的心跳。', ' ', ' ', ' ', ' ', ' ', '0');
INSERT INTO `disease` VALUES ('7', '1', '室性心律失常', '心脏', '室性心动过速是从心室发出的一种过快的心律。', ' ', '室性心律失常可能不会 引起任何症状。你可能会有 心悸——感觉心脏在快速跳 动、颤动、悸动。疲惫、 气促、头晕、胸部迫压感或心悸。', '心悸', '低剂量的日受体阻滞剂、使用电除颤来恢复正常心律、植入性心脏起搏器。', ' ', ' ', ' ', ' ', ' ', '0');
INSERT INTO `disease` VALUES ('8', '1', '房性心律失常', '心脏', '心跳会比正常时要 快，但基本保持平稳速度。', '当心脏电生理脉冲由心 房而不是窦房结引起的时 候，就会引发房性心动过速。', '房性心律失常可能不会 引起任何症状。你可能会有 心悸——感觉心脏在快速跳 动、颤动、悸动。如果心律失常使心脏泵血功 能削弱，就会让人感到疲惫、 气促、头晕、胸部迫压感或心悸。', '约 15%的脑卒中是由心房颤动 引起的。', '游走性房性起搏点和多 源性房性心动过速通常不会 引起严重的症状，也不需要 治疗。要恢复正常的心律，方法有很多种，其中最有效的 就是复律法（电复律）。通过 同步电击心脏，打破不齐心 律，恢复窦性心律。如果药物不能 有效治疗，医生会建议你采 用射频消融治疗。', '心电图', ' ', ' ', ' ', ' ', '0');
INSERT INTO `disease` VALUES ('9', '1', '心脏骤停', '心脏', '心脏骤停是指心脏突然停止泵血', '90%的心脏骤停是由室性心律失常引起的，包括室性心动过速和心室纤颤。心肌病毒感染也会引起心律失常，从而导致心脏骤停', '心脏传导阻精会使心脏停止泵血，无法维持生命；房性心律失常使心脏跳动过快而无法有效泵血。', ' ', '在等待紧急救援时，立 即使用心肺复苏（CPR）可保持部分血液灌流，直到救援到达。如果急救人员及时到达现场，他们会使用心脏除颤设备，给心肌进行电击，有时心脏能够复跳。', ' ', ' ', ' ', ' ', ' ', '0');
INSERT INTO `disease` VALUES ('10', '1', '心脏传导阻滞', '心脏', '心脏传导阻滞是指引起心脏收缩的心电脉冲从上（心房）到下（心室）的速度变缓。有时候心电信号根本元法通过，就会导致非常低的心率。', '心脏传导阻滞一般是由冠状动脉疾病或心肌病引起的。', '疲劳心率急剧下降会引起无力、头晕、晕眩甚至心搏骤停。如果你的心跳慢到无法给大脑供应足够的血 液，则会引起脑卒中。', ' ', '医生需要开展紧急治疗来恢复你的心跳。', ' ', ' ', ' ', ' ', ' ', '0');
INSERT INTO `disease` VALUES ('11', '1', '肾上腺皮质功能减退', '肾上腺', '肾上腺皮质功能减退', '肾上腺结核，自身免疫性肾上腺炎', '没有食欲。体重减轻、恶心、腹泻、呕吐,腹痛、脱水、血压低、神志不清、意识丧失', null, '治疗包括采用皮质激素类药物替代激素。\r\n药物中最常用来替代缺少的皮质醇就是氢化可的松，氟氢可的松用来取代缺少的醛固醇。', '抽血检查', ' ', ' ', ' ', ' ', '0');
INSERT INTO `disease` VALUES ('12', '1', '醛固酮增多症', '肾上腺', '肾上腺皮质分泌过量醛固酮', '肾上腺皮质分泌过量醛固酮，导致体内潴钠、排钾、血容量增多、肾素-血管紧张素系统活性受抑', '高血压，以及疲劳和肌肉无力', null, '继发性醛固酮增多症的治疗包括控制盐的摄入量，增加含有丰富钾的食物的摄入。你也可以服用利尿药安体舒通，此药干扰醛固酮对肾脏的影响，从而增加钠的排泄量，减少钾的排泄量，以此降低血压。', '抽血检查', ' ', ' ', ' ', ' ', '0');
INSERT INTO `disease` VALUES ('13', '1', '甲状腺功能亢进', '甲状腺', '甲状腺合成释放过多的甲状腺激素', '甲状腺合成释放过多的甲状腺激素', '虚弱、抑郁、腹部疼痛，或疼痛和痛苦的感觉。更严重的疾病会引起食欲不振、口渴、排尿增加、呕吐、恶心、便秘、精神错乱、记忆力减退。', null, '生可能会建议进行手术。如果切除过多腺体，可能会导致患者甲状旁腺激素不足，无法维持足够的钙水平（见“甲状旁腺功能减退症”）。在这种情况下，患者的余生都将需要钙和维生素D补充剂来增加钙的吸收。', '抽血化验', ' ', ' ', ' ', ' ', '0');
INSERT INTO `disease` VALUES ('14', '1', '甲状腺结节', '甲状腺', '甲状旁腺发育不全 甲状旁腺损伤', '甲状旁腺发育不全 甲状旁腺损伤', '手、胳膊、脚、喉咙的疼痛痉挛。其他症状包括手或脸部的麻木刺痛、头发稀薄，易患阴道真菌感染、皮肤干燥、鹅口疮。', '手或脸部的麻木刺痛、头发稀薄，易患阴道真菌感染、皮肤干燥、鹅口疮。', '注射钙对于抽搐症状的缓解立竿见影。终身补充钙与维生素D以增加钙吸收，可以完全消除症状。', '抽血化验', ' ', ' ', ' ', ' ', '0');
INSERT INTO `disease` VALUES ('15', '1', '甲状旁腺功能减退', '甲状腺', '增生性结节性甲状腺肿', '增生性结节性甲状腺肿   囊肿 ', '消瘦，肌肉虚弱无力耐热能力下降，甲状腺增大,易怒、冷漠、注意力分散反应减缓，情感迟缓，发冷，便秘，肌肉疼痛，皮肤发干、粗糙、发胀，脱发，体重增加，手指甲和脚趾甲有疼感，活动耐力下降，关节疼痛，嗓子嘶哑，月经不规律', null, '医生会先抽血检测其中促甲状腺素的水平并进行穿刺提取活组织切片检查。良性的结节不需要治疗。大的囊肿，医生会选择用注射器吸取其中的囊液破坏囊肿。如致癌的结节需要切除甲状腺。良性的结节如果产生过多的甲状腺素造成甲状腺功能亢进，通常医生会选择放射性碘治疗，而且很少会用到手术治疗。', '抽血化验', ' ', ' ', ' ', ' ', '0');
INSERT INTO `disease` VALUES ('16', '1', '甲状腺炎', '甲状腺', '靶器官因某种原因引起抗原性改变，使自已变成“非己”而加以排斥；另一是免疫活性细胞发生突变，抑制T细胞减少，B细胞失却抑制而更活跃，产生过量的抗体', '靶器官因某种原因引起抗原性改变，使自已变成“非己”而加以排斥；另一是免疫活性细胞发生突变，抑制T细胞减少，B细胞失却抑制而更活跃，产生过量的抗体', '甲状腺处有痛感，一部分会因为结节体积肿大，造成吞咽困难。桥本甲状腺炎通常会产生甲状腺的肿胀，颈部随之“肿大”，极少数还会造成呼吸、吞咽困难。\r\n亚急性甲状腺炎会造成Ф状腺局部疼痛，疼痛波及下颚、耳腔、头后部。发热和疲劳也是亚急性甲状腺炎的常见症状，而且症状常持续数月后自行消失。', '发热 疲劳', '发现有甲状腺不适、中状腺功能亢进或减退的症状要及时就医。查看病史，进行初步查看后，医生可能会检查你的甲状腺体，测Й血液中的甲状腺素，如果疑似桥本甲状腺炎的话，医生还会检查你血液中的抗体水平。治疗方法和甲状腺功能减退的治疗方法一致——需要一直服用甲状腺素药物', '抽血化验', ' ', ' ', ' ', ' ', '0');

-- ----------------------------
-- Table structure for health_information
-- ----------------------------
DROP TABLE IF EXISTS `health_information`;
CREATE TABLE `health_information` (
  `Hid` int(11) NOT NULL auto_increment,
  `Huser_id` int(11) NOT NULL,
  `Huser_height` decimal(5,2) default NULL,
  `Huser_weight` decimal(5,2) default NULL,
  `Hblood_sugar` decimal(5,2) default NULL,
  `Hblood_fat` decimal(5,2) default NULL,
  `Heart_rate` int(11) default NULL,
  `Hcholesterol_ester` decimal(5,2) default NULL,
  `Htriglyceride` decimal(5,2) default NULL,
  `Htotal_cholesterol` decimal(5,2) default NULL,
  `Hdiastolic_pressure` int(11) default NULL,
  `Hsystolic_pressure` int(11) default NULL,
  `Hear_temperature` int(11) default NULL,
  `Hrecording_date` datetime NOT NULL,
  `Hrecording_time` int(11) default NULL,
  PRIMARY KEY  (`Hid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

